import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar(props) {

  let navMenuCom = "";

  const RenderMenu = () =>{
    console.log(navMenuCom);
    if(navMenuCom){
      return(
        <>
        <li className='nav-item'>
                 <Link  to='/' className='nav-links' onClick={closeMobileMenu}>
                   Home
                 </Link>
               </li>
               <li className='nav-item'>
                 <Link  to='/service' className='nav-links' onClick={closeMobileMenu,setNavmenuCom}>
                   Services
                 </Link>
               </li>
               <li className='nav-item'>
                 <Link
                   to='/about'
                   className='nav-links'
                   onClick={closeMobileMenu}
                 >
                   About
                 </Link>
               </li>
               <li className='nav-item'>
                 <Link
                   to='/contact'
                   className='nav-links'
                   onClick={closeMobileMenu}
                 >
                   Contact
                 </Link>
               </li>

               <li>
                 <Link
                   to= {props.NavButtonlink}
                   className='nav-links'
                   onClick={closeMobileMenu}
                 >
                  <Button buttonStyle='btn--outline'>{props.NavButtonName}</Button>
                 </Link>
               </li>

        </>
      )
    }
    else{
      return(
        <>
        </>
      )
    }
  }
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);




  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const setNavmenuCom = () => navMenuCom = false;


  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>

          <i class="fa fa-id-card" aria-hidden="true"></i>
            Medical History Card

          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>

            <RenderMenu/>

          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
